import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const countries = {
  India: ["Delhi", "Mumbai", "Bangalore"],
  USA: ["New York", "Los Angeles", "Chicago"],
};

export default function SignUpForm() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: "",
    phoneCode: "+91",
    phoneNumber: "",
    country: "India",
    city: "",
    pan: "",
    aadhar: "",
  });

  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [isValid, setIsValid] = useState(false);

  const validate = () => {
    const newErrors = {};

    if (!form.firstName.trim()) newErrors.firstName = "First Name is required";
    if (!form.lastName.trim()) newErrors.lastName = "Last Name is required";
    if (!form.username.trim()) newErrors.username = "Username is required";

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(form.email)) newErrors.email = "Invalid email";

    const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*\W).{6,}$/;
    if (!passwordRegex.test(form.password))
      newErrors.password =
        "Password must have 1 uppercase, 1 digit, 1 special char, min 6 chars";

    const phoneRegex = /^\d{10}$/;
    if (!phoneRegex.test(form.phoneNumber))
      newErrors.phoneNumber = "Phone number must be 10 digits";

    if (!form.city) newErrors.city = "Please select a city";

    const panRegex = /^[A-Z]{5}\d{4}[A-Z]{1}$/;
    if (!panRegex.test(form.pan)) newErrors.pan = "Invalid PAN number";

    const aadharRegex = /^\d{12}$/;
    if (!aadharRegex.test(form.aadhar)) newErrors.aadhar = "Invalid Aadhar number";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Run validation whenever form changes, to control submit button
  useEffect(() => {
    setIsValid(validate());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [form]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    // Reset city if country changes
    if (name === "country") {
      setForm({ ...form, country: value, city: "" });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      navigate("/success", { state: form });
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 500, margin: "auto" }}>
      <h2>Sign Up</h2>

      {[
        ["First Name", "firstName"],
        ["Last Name", "lastName"],
        ["Username", "username"],
        ["Email", "email"],
        ["PAN Number", "pan"],
        ["Aadhar Number", "aadhar"],
      ].map(([label, name]) => (
        <div key={name} style={{ marginBottom: 15 }}>
          <label>{label}</label>
          <br />
          <input
            name={name}
            value={form[name]}
            onChange={handleChange}
            style={{ width: "100%", padding: 8 }}
          />
          {errors[name] && (
            <p style={{ color: "red", margin: "5px 0 0" }}>{errors[name]}</p>
          )}
        </div>
      ))}

      <div style={{ marginBottom: 15 }}>
        <label>Password</label>
        <br />
        <input
          type={showPassword ? "text" : "password"}
          name="password"
          value={form.password}
          onChange={handleChange}
          style={{ width: "80%", padding: 8 }}
        />
        <button
          type="button"
          onClick={() => setShowPassword(!showPassword)}
          style={{ marginLeft: 10, padding: "8px 12px" }}
        >
          {showPassword ? "Hide" : "Show"}
        </button>
        {errors.password && (
          <p style={{ color: "red", margin: "5px 0 0" }}>{errors.password}</p>
        )}
      </div>

      <div style={{ marginBottom: 15 }}>
        <label>Phone Number</label>
        <br />
        <input
          name="phoneCode"
          value={form.phoneCode}
          onChange={handleChange}
          style={{ width: "60px", padding: 8, marginRight: 8 }}
        />
        <input
          name="phoneNumber"
          value={form.phoneNumber}
          onChange={handleChange}
          style={{ width: "calc(100% - 78px)", padding: 8 }}
        />
        {errors.phoneNumber && (
          <p style={{ color: "red", margin: "5px 0 0" }}>{errors.phoneNumber}</p>
        )}
      </div>

      <div style={{ marginBottom: 15 }}>
        <label>Country</label>
        <br />
        <select
          name="country"
          value={form.country}
          onChange={handleChange}
          style={{ width: "100%", padding: 8 }}
        >
          {Object.keys(countries).map((c) => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: 15 }}>
        <label>City</label>
        <br />
        <select
          name="city"
          value={form.city}
          onChange={handleChange}
          style={{ width: "100%", padding: 8 }}
        >
          <option value="">-- Select City --</option>
          {countries[form.country]?.map((c) => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>
        {errors.city && (
          <p style={{ color: "red", margin: "5px 0 0" }}>{errors.city}</p>
        )}
      </div>

      <button
        type="submit"
        disabled={!isValid}
        style={{
          padding: "10px 20px",
          backgroundColor: isValid ? "#4CAF50" : "#ccc",
          color: "white",
          border: "none",
          cursor: isValid ? "pointer" : "not-allowed",
        }}
      >
        Submit
      </button>
    </form>
  );
}
