import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./style.css";

const countryOptions = {
  India: { code: "+91", cities: ["New Delhi", "Mumbai", "Bangalore", "Hyderabad", "Jaipur"] },
  USA: { code: "+1", cities: ["New York", "Los Angeles", "Chicago", "Houston", "San Francisco"] },
  UK: { code: "+44", cities: ["London", "Manchester", "Birmingham", "Liverpool"] },
  Canada: { code: "+1", cities: ["Toronto", "Vancouver", "Montreal", "Ottawa"] },
  Australia: { code: "+61", cities: ["Sydney", "Melbourne", "Brisbane", "Perth"] }
};

export default function SignUpForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: "",
    phoneCode: "",
    phoneNumber: "",
    country: "",
    city: "",
    pan: "",
    aadhar: ""
  });

  const [errors, setErrors] = useState({});
  const [cityOptions, setCityOptions] = useState([]);

  useEffect(() => {
    if (formData.country) {
      setFormData((prev) => ({
        ...prev,
        phoneCode: countryOptions[formData.country].code,
        city: ""
      }));
      setCityOptions(countryOptions[formData.country].cities);
    } else {
      setCityOptions([]);
      setFormData((prev) => ({
        ...prev,
        phoneCode: ""
      }));
    }
  }, [formData.country]);

  const validate = () => {
    const newErrors = {};
    if (!formData.firstName.trim()) newErrors.firstName = "Required";
    if (!formData.lastName.trim()) newErrors.lastName = "Required";
    if (!formData.username.trim()) newErrors.username = "Required";
    if (!formData.email.trim()) newErrors.email = "Required";
    if (!formData.password.trim()) newErrors.password = "Required";
    if (!formData.phoneNumber.trim()) newErrors.phoneNumber = "Required";
    if (!formData.country) newErrors.country = "Required";
    if (!formData.city) newErrors.city = "Required";
    if (!formData.pan.trim()) newErrors.pan = "Required";
    if (!formData.aadhar.trim()) newErrors.aadhar = "Required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      navigate("/success", { state: formData });
    }
  };

  return (
    <div className="container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={formData.firstName}
          onChange={handleChange}
        />
        {errors.firstName && <span className="error">{errors.firstName}</span>}

        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={formData.lastName}
          onChange={handleChange}
        />
        {errors.lastName && <span className="error">{errors.lastName}</span>}

        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
        />
        {errors.username && <span className="error">{errors.username}</span>}

        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        {errors.email && <span className="error">{errors.email}</span>}

        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
        />
        {errors.password && <span className="error">{errors.password}</span>}

        <div style={{ display: "flex", gap: "10px" }}>
          <input
            type="text"
            name="phoneCode"
            placeholder="Code"
            value={formData.phoneCode}
            readOnly
            style={{ width: "80px" }}
          />
          <input
            type="tel"
            name="phoneNumber"
            placeholder="Phone Number"
            value={formData.phoneNumber}
            onChange={handleChange}
          />
        </div>
        {errors.phoneNumber && <span className="error">{errors.phoneNumber}</span>}

        <select
          name="country"
          value={formData.country}
          onChange={handleChange}
        >
          <option value="">Select Country</option>
          {Object.keys(countryOptions).map((country) => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>
        {errors.country && <span className="error">{errors.country}</span>}

        <select
          name="city"
          value={formData.city}
          onChange={handleChange}
          disabled={!formData.country}
        >
          <option value="">Select City</option>
          {cityOptions.map((city) => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>
        {errors.city && <span className="error">{errors.city}</span>}

        <input
          type="text"
          name="pan"
          placeholder="PAN"
          value={formData.pan}
          onChange={handleChange}
        />
        {errors.pan && <span className="error">{errors.pan}</span>}

        <input
          type="text"
          name="aadhar"
          placeholder="Aadhar"
          value={formData.aadhar}
          onChange={handleChange}
        />
        {errors.aadhar && <span className="error">{errors.aadhar}</span>}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
