// SignUpForm.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const countryOptions = {
  India: { code: "+91", cities: ["Delhi", "Mumbai", "Bangalore", "Chennai", "Hyderabad", "Jaipur"] },
  USA: { code: "+1", cities: ["New York", "Los Angeles", "Chicago", "Houston", "San Francisco"] },
  UK: { code: "+44", cities: ["London", "Manchester", "Birmingham", "Liverpool"] },
  Australia: { code: "+61", cities: ["Sydney", "Melbourne", "Brisbane", "Perth"] },
  Canada: { code: "+1", cities: ["Toronto", "Vancouver", "Montreal", "Ottawa"] }
};

const SignUpForm = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: "",
    phoneCode: "",
    phoneNumber: "",
    country: "",
    city: "",
    pan: "",
    aadhar: "",
  });

  const [formErrors, setFormErrors] = useState({});
  const [isFormValid, setIsFormValid] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const validateFields = () => {
    const errors = {};
    if (!formData.firstName.trim()) errors.firstName = "First Name is required";
    if (!formData.lastName.trim()) errors.lastName = "Last Name is required";
    if (!formData.username.trim()) errors.username = "Username is required";

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) errors.email = "Enter a valid email";

    const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*\W).{6,}$/;
    if (!passwordRegex.test(formData.password)) {
      errors.password = "Password must include uppercase, digit, special char, and be 6+ chars";
    }

    const phoneRegex = /^\d{10}$/;
    if (!phoneRegex.test(formData.phoneNumber)) errors.phoneNumber = "Phone number must be 10 digits";

    if (!formData.country) errors.country = "Please select a country";
    if (!formData.city) errors.city = "Please select a city";

    const panRegex = /^[A-Z]{5}\d{4}[A-Z]$/;
    if (!panRegex.test(formData.pan)) errors.pan = "Invalid PAN format";

    const aadharRegex = /^\d{12}$/;
    if (!aadharRegex.test(formData.aadhar)) errors.aadhar = "Aadhar must be 12 digits";

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  useEffect(() => {
    setIsFormValid(validateFields());
  }, [formData]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "country") {
      setFormData((prev) => ({
        ...prev,
        country: value,
        phoneCode: countryOptions[value]?.code || "",
        city: "",
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateFields()) {
      navigate("/success", { state: formData });
    }
  };

  const renderInput = (label, name, type = "text") => (
    <div style={{ marginBottom: 15 }}>
      <label>{label}</label><br />
      <input
        type={type}
        name={name}
        value={formData[name]}
        onChange={handleChange}
        style={{ width: "100%", padding: 8 }}
      />
      {formErrors[name] && <p style={{ color: "red" }}>{formErrors[name]}</p>}
    </div>
  );

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 500, margin: "auto" }}>
      <h2 style={{ textAlign: "center" }}>Create Your Account</h2>

      {renderInput("First Name", "firstName")}
      {renderInput("Last Name", "lastName")}
      {renderInput("Username", "username")}
      {renderInput("Email", "email")}
      {renderInput("PAN Number", "pan")}
      {renderInput("Aadhar Number", "aadhar")}

      <div style={{ marginBottom: 15 }}>
        <label>Password</label><br />
        <input
          type={showPassword ? "text" : "password"}
          name="password"
          value={formData.password}
          onChange={handleChange}
          style={{ width: "80%", padding: 8 }}
        />
        <button
          type="button"
          onClick={() => setShowPassword((prev) => !prev)}
          style={{ marginLeft: 10, padding: "8px 12px" }}
        >
          {showPassword ? "Hide" : "Show"}
        </button>
        {formErrors.password && <p style={{ color: "red" }}>{formErrors.password}</p>}
      </div>

      <div style={{ marginBottom: 15 }}>
        <label>Phone Number</label><br />
        <input
          name="phoneCode"
          value={formData.phoneCode}
          disabled
          style={{ width: 60, padding: 8, marginRight: 8 }}
        />
        <input
          name="phoneNumber"
          value={formData.phoneNumber}
          onChange={handleChange}
          style={{ width: "calc(100% - 78px)", padding: 8 }}
        />
        {formErrors.phoneNumber && <p style={{ color: "red" }}>{formErrors.phoneNumber}</p>}
      </div>

      <div style={{ marginBottom: 15 }}>
        <label>Country</label><br />
        <select
          name="country"
          value={formData.country}
          onChange={handleChange}
          style={{ width: "100%", padding: 8 }}
        >
          <option value="">-- Select Country --</option>
          {Object.keys(countryOptions).map((country) => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>
        {formErrors.country && <p style={{ color: "red" }}>{formErrors.country}</p>}
      </div>

      <div style={{ marginBottom: 15 }}>
        <label>City</label><br />
        <select
          name="city"
          value={formData.city}
          onChange={handleChange}
          style={{ width: "100%", padding: 8 }}
          disabled={!formData.country}
        >
          <option value="">-- Select City --</option>
          {(countryOptions[formData.country]?.cities || []).map((city) => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>
        {formErrors.city && <p style={{ color: "red" }}>{formErrors.city}</p>}
      </div>

      <button
        type="submit"
        disabled={!isFormValid}
        style={{
          width: "100%",
          padding: "10px 20px",
          backgroundColor: isFormValid ? "#4CAF50" : "#ccc",
          color: "white",
          border: "none",
          cursor: isFormValid ? "pointer" : "not-allowed",
        }}
      >
        Submit
      </button>
    </form>
  );
};

export default SignUpForm;
