import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./style.css";

const countryOptions = {
  India: { code: "+91", cities: ["New Delhi", "Mumbai", "Bangalore", "Hyderabad", "Jaipur"] },
  USA: { code: "+1", cities: ["New York", "Los Angeles", "Chicago", "Houston", "San Francisco"] },
  UK: { code: "+44", cities: ["London", "Manchester", "Birmingham", "Liverpool"] },
  Canada: { code: "+1", cities: ["Toronto", "Vancouver", "Montreal", "Ottawa"] },
  Australia: { code: "+61", cities: ["Sydney", "Melbourne", "Brisbane", "Perth"] }
};

export default function SignUpForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: "",
    phoneCode: "",
    phoneNumber: "",
    country: "",
    city: "",
    pan: "",
    aadhar: ""
  });

  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [cityOptions, setCityOptions] = useState([]);

  useEffect(() => {
    if (formData.country) {
      setFormData((prev) => ({
        ...prev,
        phoneCode: countryOptions[formData.country].code,
        city: ""
      }));
      setCityOptions(countryOptions[formData.country].cities);
    } else {
      setCityOptions([]);
      setFormData((prev) => ({
        ...prev,
        phoneCode: ""
      }));
    }
  }, [formData.country]);

  const validate = () => {
    const newErrors = {};
    if (!formData.firstName.trim()) newErrors.firstName = "First name is required";
    if (!formData.lastName.trim()) newErrors.lastName = "Last name is required";
    if (!formData.username.trim()) newErrors.username = "Username is required";

    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(formData.email)) {
      newErrors.email = "Invalid email format";
    }

    if (!formData.password.trim()) {
      newErrors.password = "Password is required";
    } else if (
      !/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(formData.password)
    ) {
      newErrors.password =
        "Min 8 chars, 1 uppercase, 1 lowercase, 1 digit, 1 special char";
    }

    if (!formData.phoneNumber.trim()) {
      newErrors.phoneNumber = "Phone number is required";
    } else if (!/^\d{10}$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = "Must be 10 digits";
    }

    if (!formData.country) newErrors.country = "Country is required";
    if (!formData.city) newErrors.city = "City is required";

    if (!formData.pan.trim()) {
      newErrors.pan = "PAN is required";
    } else if (!/^[A-Z]{5}[0-9]{4}[A-Z]$/.test(formData.pan)) {
      newErrors.pan = "Invalid PAN format";
    }

    if (!formData.aadhar.trim()) {
      newErrors.aadhar = "Aadhar is required";
    } else if (!/^\d{12}$/.test(formData.aadhar)) {
      newErrors.aadhar = "Aadhar must be 12 digits";
    }

    return newErrors;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleBlur = (e) => {
    const { name } = e.target;
    setTouched((prev) => ({ ...prev, [name]: true }));
    const newErrors = validate();
    setErrors(newErrors);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validate();
    setErrors(validationErrors);
    setTouched({
      firstName: true, lastName: true, username: true, email: true, password: true,
      phoneNumber: true, country: true, city: true, pan: true, aadhar: true
    });

    if (Object.keys(validationErrors).length === 0) {
      navigate("/success", { state: formData });
    }
  };

  return (
    <div className="container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={formData.firstName}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {touched.firstName && errors.firstName && <span className="error">{errors.firstName}</span>}

        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={formData.lastName}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {touched.lastName && errors.lastName && <span className="error">{errors.lastName}</span>}

        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {touched.username && errors.username && <span className="error">{errors.username}</span>}

        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {touched.email && errors.email && <span className="error">{errors.email}</span>}

        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {touched.password && errors.password && <span className="error">{errors.password}</span>}

        <div style={{ display: "flex", gap: "10px" }}>
          <input
            type="text"
            name="phoneCode"
            placeholder="Code"
            value={formData.phoneCode}
            readOnly
            style={{ width: "80px" }}
          />
          <input
            type="tel"
            name="phoneNumber"
            placeholder="Phone Number"
            value={formData.phoneNumber}
            onChange={handleChange}
            onBlur={handleBlur}
          />
        </div>
        {touched.phoneNumber && errors.phoneNumber && <span className="error">{errors.phoneNumber}</span>}

        <select
          name="country"
          value={formData.country}
          onChange={handleChange}
          onBlur={handleBlur}
        >
          <option value="">Select Country</option>
          {Object.keys(countryOptions).map((country) => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>
        {touched.country && errors.country && <span className="error">{errors.country}</span>}

        <select
          name="city"
          value={formData.city}
          onChange={handleChange}
          onBlur={handleBlur}
          disabled={!formData.country}
        >
          <option value="">Select City</option>
          {cityOptions.map((city) => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>
        {touched.city && errors.city && <span className="error">{errors.city}</span>}

        <input
          type="text"
          name="pan"
          placeholder="PAN"
          value={formData.pan}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {touched.pan && errors.pan && <span className="error">{errors.pan}</span>}

        <input
          type="text"
          name="aadhar"
          placeholder="Aadhar"
          value={formData.aadhar}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {touched.aadhar && errors.aadhar && <span className="error">{errors.aadhar}</span>}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
